import './App.css';
import React, { useState } from 'react';

function App() {
  const [calc, setCalc] = useState('');
  const [result, setResult] = useState('');

  const ops = ['+', '-', '*', '/', '.'];

  const createDigits = () => {
    const digits = [];

    for (let i = 1; i < 10; i++) {
      digits.push( <button key={i}>{i}</button> );
    }
    return digits;
  };

  return (
    <div className="App">
      <div className="calc">
        <div className="display">
          <span>0</span> {0}
        </div>
        <div className="operators">
          <button>+</button>
          <button>-</button>
          <button>*</button>
          <button>/</button>
          <button>DEL</button>
        </div>
        <div className="digits">
          <button>0</button>
          <button>.</button>
          <button>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
