import './App.css';
import React, { useState } from 'react';

function App() {
  const [calc, setCalc] = useState('');
  const [result, setResult] = useState('');

  const ops = ['+', '-', '*', '/', '.'];

  const createDigits = () => {
    const digits = [];

    for (let i = 1; i < 10; i++) {
      digits.push(<button onClick={() =>
        updateCalc(i.toString())}
        key={i}>{i}</button>);
    }
    return digits;
  };

  const updateCalc = value => {
    if (
      ops.includes(value) && calc === '' ||
      ops.includes(value) && ops.includes(calc.slice(-1))
    ) {
      return;
    }

    setCalc(calc + value);

    if (!ops.includes(value)) {
      setResult(eval(calc + value).toString());
    }
  }

  return (
    <div className="App">
      <div className="calc">
        <div className="display">
          <span>{result ? '(' + result + ')' : ''}
          </span> {calc || 0}
        </div>
        <div className="operators">
          <button>+</button>
          <button>-</button>
          <button>*</button>
          <button>/</button>
          <button>DEL</button>
        </div>
        <div className="digits">
          <button>0</button>
          <button>.</button>
          <button>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
